ocket\"   \n[25] \"reentri\"   \"much\"      \"tougher\"   \"time\"      \"around\"    \"due\"      \n[31] \"deep\"      \"space\"     \"mission\"   \"almost\"    \"\"          \"x\"        \n[37] \"forc\"      \"\"          \"x\"         \"heat\"      \"plenti\"    \"hydraul\"  \n[43] \"fluid\"     \"tho\"       \"launch\"    \"st\"        \"deep\"      \"space\"    \n[49] \"mission\"   \"today\"     \"head\"      \"earthsun\"  \"l\"         \"graviti\"  \n[55] \"null\"      \"point\"     \"\"          \"m\"         \"mile\"      \"\"         \n[61] \"x\"         \"moon\"     \n\n[[3]]\n [1] \"launch\"     \"postpon\"    \"tomorrow\"   \"due\"        \"high\"      \n [6] \"wind\"       \"cape\"       \"dragon\"     \"still\"      \"inbound\"   \n[11] \"orbit\"      \"\"           \"min\"        \"extrem\"     \"wind\"      \n[16] \"share\"      \"cape\"       \"canaver\"    \"feel\"       \"like\"      \n[21] \"sledgehamm\" \"superson\"   \"vertic\"     \"hope\"       \"chang\"     \n[26] \"\"           \"civilian\"   \"applic\"    \n\n[[4]]\n [1] \"rocket\"               \"soft\"                 \"land\"                \n [4] \"ocean\"                \"with\"                 \"\"                    \n [7] \"m\"                    \"target\"               \"nice\"                \n[10] \"vertic\"               \"high\"                 \"probabl\"             \n[13] \"good\"                 \"droneship\"            \"land\"                \n[16] \"nonstormi\"            \"weather\"              \"primari\"             \n[19] \"mission\"              \"target\"               \"spacecraft\"          \n[22] \"head\"                 \"toward\"               \"sun\"                 \n[25] \"good\"                 \"daniellocky\"          \"actual\"              \n[28] \"thatmayb\"             \"plan\"                 \"signific\"            \n[31] \"upgrad\"               \"droneship\"            \"futur\"               \n[34] \"mission\"              \"handl\"          