]\n  but_count_list[[length(but_count_list) + 1]] = sentiment[\"but_count\"]\n}\n\n# Combine lists into data frame\nvader_df = data.frame(\n  word_scores = unlist(word_scores_list),\n  compound = unlist(compound_list),\n  pos = unlist(pos_list),\n  neu = unlist(neu_list),\n  neg = unlist(neg_list),\n  but_count = unlist(but_count_list)\n)\n\ncolnames(vader_df)","metadata":{"execution":{"iopub.status.busy":"2023-04-14T00:38:32.390950Z","iopub.execute_input":"2023-04-14T00:38:32.392516Z","iopub.status.idle":"2023-04-14T00:41:38.226906Z"},"trusted":true},"execution_count":36,"outputs":[{"output_type":"display_data","data":{"text/html":"<style>\n.list-inline {list-style: none; margin:0; padding: 0}\n.list-inline>li {display: inline-block}\n.list-inline>li:not(:last-child)::after {content: \"\\00b7\"; padding: 0 .5ex}\n</style>\n<ol class=list-inline><li>'word_scores'</li><li>'compound'</li><li>'pos'</li><li>'neu'</li><li>'neg'</li><li>'but_count'</li></ol>\n","text/markdown":"1. 'word_scores'\n2. 'compound'\n3. 'pos'\n4. 'neu'\n5. 'neg'\n6. 'but_count'\n\n\n","text/latex":"\\begin{enumerate*}\n\\item 'word\\_scores'\n\\item 'compound'\n\\item 'pos'\n\\item 'neu'\n\\item 'neg'\n\\item 'but\\_count'\n\\end{enumerate*}\n","text/plain":"[1] \"word_scores\" \"compound\"    \"pos\"         \"neu\"         \"neg\"        \n[6] \"but_count\"  "},"metadata":{}}]},{"cell_type":"markdown","source":"## Merging Data","metadata":{}},{"cell_type":"code","source":"tesla_sentiment = cbind(tesla_tfidf, vader_df)\nprint(nrow(tesla_sentiment))\nhead(tesla_sentiment, 5)","metadata":{"execution":{"iopub.status.busy":"2023-04-14T00:41:38.228804Z","iopub.execute_input":"2023-04-14T00:41:38.229772Z","iopub.status.idle":"2023-04-14T00:41:38.307835Z"},"trusted":true},"execution_count":37,"outputs":[{"name":"stdout","text":"[1] 1360\n","output_type":"stream"},{"output_type":"display_data","data":{"text/html":"<table class=\"dataframe\">\n<caption>A data.frame: 5 √ó 133</caption>\n<thead>\n\t<tr><t